import { createAction } from 'redux-actions';
import { SET_CURRENT_PAGE_NUMBER, SET_DOCUMENTS_NEXT_TOKEN, SET_SEARCH_QUERY, SET_SEARCH_STATUS, CLEAR_SEARCH_QUERY, SET_SEARCH_PERSONA, SET_DOCUMENT_SEARCH_QUERY, CLEAR_DOCUMENT_SEARCH_QUERY } from '../../../constants/action-types';
/**
 * Set the current page number
 */

export var setCurrentPageNumber = createAction(SET_CURRENT_PAGE_NUMBER, function (currentPageNumber) {
  return {
    meta: {
      currentPageNumber: currentPageNumber
    }
  };
});
/**
 * Set the documents next token
 */

export var setDocumentsNextToken = createAction(SET_DOCUMENTS_NEXT_TOKEN, function (documentsNextToken) {
  return {
    meta: {
      documentsNextToken: documentsNextToken
    }
  };
});
/**
 * Set the search query
 */

export var setSearchQuery = createAction(SET_SEARCH_QUERY, function (searchQuery) {
  return {
    meta: {
      searchQuery: searchQuery.replace(/ {2,}/g, ' ')
    }
  };
});
/**
 * Clear the search query
 */

export var clearSearchQuery = createAction(CLEAR_SEARCH_QUERY, function () {
  return {
    meta: {
      searchQuery: ''
    }
  };
});
/**
 * Set the search query
 */

export var setDocumentSearchQuery = createAction(SET_DOCUMENT_SEARCH_QUERY, function (searchQuery) {
  return {
    meta: {
      documentSearchQuery: searchQuery.replace(/ {2,}/g, ' ')
    }
  };
});
/**
 * Clear the search query
 */

export var clearDocumentSearchQuery = createAction(CLEAR_DOCUMENT_SEARCH_QUERY, function () {
  return {
    meta: {
      documentSearchQuery: ''
    }
  };
});
/**
 * Set the search status
 */

export var setSearchStatus = createAction(SET_SEARCH_STATUS, function (searchStatus) {
  return {
    meta: {
      searchStatus: searchStatus
    }
  };
});
export var setSearchPersona = createAction(SET_SEARCH_PERSONA, function (persona) {
  return {
    meta: {
      searchPersona: persona
    }
  };
});
